import Link from "next/link";
import Image from "next/image";
import { ThemeToggle } from "./theme-toggle";

const navItems = [
  { name: "Blog", href: "/blog", alt: "Blog" },
  { name: "Kho√° h·ªçc", href: "/courses", alt: "Kho√° h·ªçc" },
  { name: "Series", href: "/series", alt: "Series" },
  { name: "Tags", href: "/tags", alt: "Tags" },
  { name: "Toolbox", href: "https://github.com/vntechies/toolbox", alt: "Toolbox", external: true },
];

export default function Header() {
  return (
    <header className="flex items-center justify-between py-10">
      <div>
        <Link href="/" alt="Trang ch·ªß" aria-label="VNTechies Dev Blog">
          <div className="flex items-center justify-between">
            <div className="mr-3">
              <Image 
                src="https://ext.same-assets.com/1980811572/2533966128.webp" 
                alt="VNTechies Logo" 
                width={40} 
                height={40} 
              />
            </div>
            <span className="hidden h-6 text-2xl font-semibold sm:block text-orange-500">
              VNTECHIES
            </span>
          </div>
        </Link>
      </div>
      <div className="flex items-center text-base leading-5">
        <div className="hidden sm:block">
          {navItems.map((item) => (
            item.external ? (
              <a 
                key={item.name} 
                target="_blank" 
                rel="noopener noreferrer" 
                href={item.href} 
                alt={item.alt}
                className="rounded px-5 py-1 text-gray-900 hover:bg-gray-200 dark:text-gray-100 dark:hover:bg-gray-700 sm:px-3 sm:py-2 font-medium text-zinc-600 dark:text-zinc-400"
